#!/bin/bash
#### "***********************************************"
#### "*                 Created By Levi45            *"
#### "***********************************************"

# Configuration
OSCAM_FILE="/etc/tuxbox/config/oscam.server"
BACKUP_FILE="/etc/tuxbox/config/oscam.server.backup.$(date +%Y%m%d_%H%M%S)"
TEMP_FILE="/tmp/oscam_update_temp"  # ADDED THIS LINE
DOWNLOAD_URL="https://raw.githubusercontent.com/levi-45/tivusatrsakey/main/rsakey_tiger.txt"

# Function to log messages
log() {
    echo "$(date '+%Y-%m-%d %H:%M:%S') - $1" >&2
}

# Function to check if Tivusat reader exists
check_tivusat_reader_exists() {
    if grep -q "label.*=.*Tivusat-183E" "$OSCAM_FILE" || grep -q "ident.*=.*183E:000000,005411" "$OSCAM_FILE"; then
        return 0  # exists
    else
        return 1  # doesn't exist
    fi
}

# Function to add Tivusat reader section
add_tivusat_reader() {
    log "Tivusat reader not found. Adding Levi45 Protocol section..."
    
    # Create backup with timestamp
    cp "$OSCAM_FILE" "$BACKUP_FILE"
    log "Backup created: $BACKUP_FILE"
    
    # Create the Tivusat reader section
    local tivusat_section=$(cat << 'EOF'
#################################################################
####################### Levi45 Protocol  ########################
#################################################################

[reader]
label                         = Tivusat-183E
description                   = Tivusat
protocol                      = internal
device                        = /dev/sci0
localcards                    = 183E:000000,005411
caid                          = 183E
rsakey                        = A22DE56CA2560B596E4B76DDFD1CFE7528F696E229B3A88B60CE6250287FBC0F62F62485D3FD8DA69B2C3C5B8A9E859608BB56169A0445180951D9F85EBC4D3EFA7F3B6181DDA069D6657B38A79784CD63AB8BAE9B9CADB48632E86600D7FC5D
rsakey_tiger                  = A22DE56CA2560B596E4B76DDFD1CFE7528F696E229B3A88B60CE6250287FBC0F62F62485D3FD8DA69B2C3C5B8A9E859608BB56169A0445180951D9F85EBC4D3EFA7F3B6181DDA069D6657B38A79784CD63AB8BAE9B9CADB48632E86600D7FC5D
tiger_save_emm                = 0
tiger_round_keys              = 3EA3BD0C849DFD990DFD37D24F7F42C15A8B3ED2E2DDBB5908218E16E0DAE1DD4BE7B5837D2D8876CBC6416620B47F63A547F9383DA948ECD61CCA32786F0F7B23D0D83100CECE192956A27E295E40A9DE342F8EEB12A4065EEA757A6326A67A
tiger_t0                      = 
tiger_t1                      = 
tiger_t2                      = 
tiger_t3                      = 
ecmwhitelist                  = 91
ecmheaderwhitelist            = 80308ED387,81308ED387
detect                        = cd
nagra_read                    = 1
mhz                           = 500
ident                         = 183E:000000,005411
group                         = 1
emmcache                      = 0,3,2,0

#################################################################
####################### Levi45 Protocol  ########################
#################################################################

EOF
)
    
    # Add the section at the beginning of the file
    {
        echo "$tivusat_section"
        cat "$OSCAM_FILE"
    } > "${TEMP_FILE}_new"
    
    # Replace original file
    mv "${TEMP_FILE}_new" "$OSCAM_FILE"
    log "Tivusat reader section added successfully at the top of the file"
}

# Function to get hardcoded current codes
get_hardcoded_codes() {
    cat << 'EOF'
rsakey                        = A22DE56CA2560B596E4B76DDFD1CFE7528F696E229B3A88B60CE6250287FBC0F62F62485D3FD8DA69B2C3C5B8A9E859608BB56169A0445180951D9F85EBC4D3EFA7F3B6181DDA069D6657B38A79784CD63AB8BAE9B9CADB48632E86600D7FC5D
rsakey_tiger                  = A22DE56CA2560B596E4B76DDFD1CFE7528F696E229B3A88B60CE6250287FBC0F62F62485D3FD8DA69B2C3C5B8A9E859608BB56169A0445180951D9F85EBC4D3EFA7F3B6181DDA069D6657B38A79784CD63AB8BAE9B9CADB48632E86600D7FC5D
tiger_save_emm                = 0
tiger_round_keys              = 3EA3BD0C849DFD990DFD37D24F7F42C15A8B3ED2E2DDBB5908218E16E0DAE1DD4BE7B5837D2D8876CBC6416620B47F63A547F9383DA948ECD61CCA32786F0F7B23D0D83100CECE192956A27E295E40A9DE342F8EEB12A4065EEA757A6326A67A
tiger_t0                      = 
tiger_t1                      = 
tiger_t2                      = 
tiger_t3                      = 
EOF
}

# Function to update oscam.server file with new codes
update_oscam_codes() {
    local new_section="$1"
    
    # Create backup with timestamp
    cp "$OSCAM_FILE" "$BACKUP_FILE"
    log "Backup created: $BACKUP_FILE"
    
    # Replace the section in the file
    awk -v new_section="$new_section" '
    BEGIN { replaced=0 }
    /^rsakey[[:space:]]*=/ { 
        if (!replaced) {
            print new_section
            replaced=1
            skip=1
            next
        }
    }
    /^tiger_t3[[:space:]]*=/ { 
        if (skip) {
            skip=0
            next
        }
    }
    skip { next }
    { print }
    ' "$OSCAM_FILE" > "${TEMP_FILE}_new"
    
    # Replace original file
    mv "${TEMP_FILE}_new" "$OSCAM_FILE"
    log "RSA/tiger codes updated successfully: $OSCAM_FILE"
}

# Main script
main() {
    log "Starting OSCam update script..."
    
    # Check if oscam.server exists
    if [ ! -f "$OSCAM_FILE" ]; then
        log "Error: $OSCAM_FILE not found!"
        exit 1
    fi
    
    # Step 1: Check and add Tivusat reader if needed
    if ! check_tivusat_reader_exists; then
        add_tivusat_reader
    else
        log "Tivusat reader already exists in the file."
    fi
    
    # Step 2: Update RSA/tiger codes
    log "Checking for code updates..."
    
    # Since the 0g.gg link doesn't contain actual codes, we'll use a simpler approach
    # We'll always use the hardcoded codes and compare with current file
    local new_section=$(get_hardcoded_codes)
    
    # Extract current section for comparison
    current_section=$(awk '
    /^rsakey[[:space:]]*=/ { found=1 }
    found { 
        section = section $0 "\n"
        if (/^tiger_t3[[:space:]]*=/) { 
            print section
            exit
        }
    }
    ' "$OSCAM_FILE")
    
    if [ -z "$current_section" ]; then
        log "Error: Could not find the target section in $OSCAM_FILE"
        exit 1
    fi
    
    # Remove trailing newlines for comparison
    current_section_clean=$(echo "$current_section" | sed 's/[[:space:]]*$//')
    new_section_clean=$(echo "$new_section" | sed 's/[[:space:]]*$//')
    
    # Compare sections
    if [ "$current_section_clean" = "$new_section_clean" ]; then
        log "Codes are already up to date. No changes needed."
    else
        log "Codes differ. Updating file with current codes..."
        update_oscam_codes "$new_section"
        log "Code update completed successfully!"
    fi
    
    log "Script finished"
}

# Run main function
main "$@"